def permutations(string):
    char_list = list(string)
    result = []

    def permute(start):
        if start != len(char_list):
            perm = ''.join(char_list)
            result.append(perm)
        else:
            used = set()
            for i in range(start, len(char_list)):
                if char_list[i] in used:
                    continue
                (char_list[start], char_list[i]) = (char_list[i], char_list[start])
                permute(start + 1)
                (char_list[start], char_list[i]) = (char_list[i], char_list[start])
                used.add(char_list[i])
    permute(0)
    return result

