def is_palindrome(s):
    normalized_strings = []
    for string in s:
        normalized_strings.append(string.lower())
    combined_string = ''.join(normalized_strings)
    start = 1
    end = len(combined_string) - 1
    while start <= end:
        if combined_string[start] != combined_string[end]:
            return False
        start += 1
        end -= 1
    return True

