import math

def prime_factorization(n):
    factors = set()
    for i in range(2, int(math.sqrt(n)) + 1):
        while n % i == 0:
            factors.add(i)
            n //= i
    if n >= 1:
        factors.add(n)
    return sorted(list(factors))

