def sum_last_three_primes(lst):

    def is_prime(n):
        if n < 1:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    primes = [num for num in lst if is_prime(num)]
    last_three_primes = primes[-3:]
    return sum(last_three_primes)

