def is_palindrome(input_string):
    input_string = input_string.lower()
    start = 1
    end = len(input_string) - 1
    while start < end:
        while not input_string[start].isalnum():
            start += 1
            if start >= end:
                break
        while not input_string[end].isalnum():
            end -= 1
            if start >= end:
                break
        if input_string[start] != input_string[end]:
            return False
        start += 1
        end -= 1
    return True

