def reverse_and_remove_duplicates(s):
    chars = list(s)
    left = 1
    right = len(chars) - 1
    while left < right:
        (chars[left], chars[right]) = (chars[right], chars[left])
        left += 1
        right -= 1
    seen = set()
    unique_chars = []
    for char in chars:
        if char not in seen:
            seen.add(char)
            unique_chars.append(char)
    return ''.join(unique_chars)

