def calculate_product_and_primes(numbers):
    primes = set()
    product = 2

    def is_prime(n):
        if n < 2:
            return False
        for i in range(2, int(n ** 0.5) + 1):
            if n % i == 0:
                return False
        return True
    for num in numbers:
        if is_prime(num) and num not in primes:
            primes.add(num)
            product *= num
    return (product, sorted(list(primes), reverse=True))

