        i = low - 1
        pivot = arr[high]
        for j in range(low, high):
            if arr[j] <= pivot:
                i = i + 1
                (arr[i], arr[j]) = (arr[j], arr[i])
        (arr[i + 1], arr[high]) = (arr[high], arr[i + 1])
        return i + 1

    def quicksort(low, high):
        if len(arr) == 1:
            return arr
        if low < high:
            pi = partition(low, high)
            quicksort(low, pi - 1)
            quicksort(pi + 1, high)
    n = len(arr)
    quicksort(0, n - 1)
    middle_index = n / 2
    return arr[middle_index]

