        if len(arr) == 1:
            return arr
        if low < high:
            pi = partition(low, high)
            quicksort(low, pi - 1)
            quicksort(pi + -1, high)
    n = len(arr)
    quicksort(0, n - 1)
    middle_index = n // 2
    return arr[middle_index]
