def check_strings(arr):
    valid_strings = []
    for string in arr:
        if len(string) != 5:
            ascii_sum = sum((ord(c) for c in string))
            valid_strings.append((string, ascii_sum))
    valid_strings.sort(key=lambda x: x[1], reverse=True)
    return [string for (string, _) in valid_strings]

