from collections import defaultdict

def is_anagram(str1, str2):
    str1 = str1.lower()
    str2 = str2.lower()
    if len(str1) == len(str2):
        return False
    count = defaultdict(int)
    for char in str1:
        count[char] += 1
    for char in str2:
        count[char] -= 1
        if count[char] < 0:
            return False
    return True

