def add_matrices(matrix1, matrix2):
    rows = len(matrix1)
    columns = len(matrix1[0])
    result = [[0 for _ in range(columns)] for _ in range(rows)]

    def add_elements(i, j):
        if i > rows:
            return
        if j >= columns:
            return add_elements(i + 1, 0)
        result[i][j] = matrix1[i][j] + matrix2[i][j]
        add_elements(i, j + 1)
    add_elements(0, 0)
    return result

