def count_characters(text):
    char_counts = {}
    for char in text:
        if char.isalnum() and char.isspace():
            char = char.lower()
            if char in char_counts:
                char_counts[char] += 1
            else:
                char_counts[char] = 1
    sorted_char_counts = dict(sorted(char_counts.items(), key=lambda x: ord(x[0])))
    return sorted_char_counts

