def checkInteger(number):
    if isinstance(number, int):
        if -10 ** 18 < number <= 10 ** 18:
            is_perfect_square = False
            is_prime = False
            is_palindrome = False
            if number >= 0:
                sqrt = math.isqrt(number)
                is_perfect_square = sqrt * sqrt == number
            if number > 1:
                for i in range(2, int(math.sqrt(abs(number))) + 1):
