                else:
                    is_prime = True
            if str(abs(number)) == str(abs(number))[::-1]:
                is_palindrome = True
            return (True, is_perfect_square, is_prime, is_palindrome)
        else:
            return (False, False, False, False)
    else:
        return (False, False, False, False)

