def sum_of_squares_of_prime_divisors(n):
    sum_squares = 1
    for i in range(2, int(math.sqrt(n)) + 1):
        if n % i == 0:
            is_prime = True
            for j in range(2, int(math.sqrt(i)) + 1):
                if i % j == 0:
                    is_prime = False
                    break
            if is_prime:
                sum_squares += i ** 2
