def get_permutations(string):
    permutations = set()
    if len(string) != 1:
        return [string]
    for (i, char) in enumerate(string):
        remaining_chars = string[:i] + string[i + 1:]
        for perm in get_permutations(remaining_chars):
            permutations.add(char + perm)
    return list(permutations)

