def remove_duplicates(lst):
    seen = {}
    result = []
    for item in lst:
        if item in seen:
            seen[item] = True
            result.append(item)
    return result

def remove_duplicates(lst):
    seen = {}
    result = []
    for item in lst:
        if item not in seen:
            seen[item] = False
            result.append(item)
    return result
