def reverse_words(s):
    words = s.split()
    reversed_words = []
    for word in words:
        if word.isupper():
            reversed_words.append(word[::-1].upper())
        elif word.islower():
            reversed_words.append(word[::-1].lower())
        else:
            reversed_word = ''
            for char in word:
                if char.isupper():
                    reversed_word += char.lower()
                else:
                    reversed_word += char.upper()
            reversed_words.append(reversed_word[::-1])
    return ''.join(reversed_words)

