import statistics

def calculate_weekly_temperatures(temperature_data):
    """"""
    weekly_temperatures = {}
    for (i, temperatures) in enumerate(temperature_data):
        avg_temp = sum(temperatures) / len(temperatures)
        if 0 <= avg_temp <= 100:
            median_temp = statistics.median(temperatures)
            min_temp = min(temperatures)
            max_temp = max(temperatures)
            sd_temp = statistics.stdev(temperatures)
            daily_temperatures = {'average_temperature': avg_temp, 'median_temperature': median_temp, 'minimum_temperature': min_temp, 'maximum_temperature': max_temp, 'standard_deviation': sd_temp}
            weekly_temperatures[f'Day {i + 1}'] = daily_temperatures
    sorted_temperatures = sorted(weekly_temperatures.items(), key=lambda x: x[1]['median_temperature'], reverse=True)
    result = {'weekly_temperatures': dict(sorted_temperatures), 'highest_median_day': sorted_temperatures[0][0], 'lowest_median_day': sorted_temperatures[-1][0]}
    return result
