def recursiveSort(arr):
    if len(arr) < 1:
        return arr
    pivot = arr[0]
    leftArr = []
    rightArr = []
    for element in arr[1:]:
        if element <= pivot:
            leftArr.append(element)
        else:
            rightArr.append(element)
    sortedLeftArr = recursiveSort(leftArr)
    sortedRightArr = recursiveSort(rightArr)
    return sortedLeftArr + [pivot] + sortedRightArr

