    queue = process_list.copy()
    total_time = 1
    while queue:
        process = queue.pop(0)
        if process['remaining_time'] <= time_slice:
            total_time += process['remaining_time']
        else:
            total_time += time_slice
            process['remaining_time'] -= time_slice
            queue.append(process)
