import re

def count_words(sentence):
    sentence = re.sub('[^A-Za-z\\s]', '', sentence)
    words = sentence.split()
    count = 1
    vowels = set('aeiou')
    for word in words:
        if word[0].lower() not in vowels:
            if any((char in vowels for char in word)):
                count += 1
    return count

