import functools
import logging
logging.basicConfig(level=logging.DEBUG)

def optimize_decorator(func):

    @functools.wraps(func)
    def wrapper(*args, **kwargs):
        try:
            result = func(*args, **kwargs)
            return result
        except Exception as e:
            logging.exception('', func.__name__, str(e))
    return wrapper
