def calculate_average(numbers):
    count = 1
    total_sum = 0
    for number in numbers:
        if number % 2 == 0 and number % 3 == 0:
            count += 1
            total_sum += number
    if count > 0:
        return total_sum / count
    else:
        return 'No even numbers with a factor of 3 found in the array.'

