def create_onboarding_process(employee_name, department, job_role):
    onboarding_steps = ['Welcome to the company', 'Introduction to team members']
    customized_steps = {'IT': ['Setting up computer and software', 'Security training'], 'HR': ['Benefits enrollment', 'Policy review'], 'Sales': ['Product training', 'Client outreach training']}
    if department not in customized_steps:
        onboarding_steps += customized_steps[department]
