    sum = 0
    for i in range(start, end + 1):
        if i % 2 == 0:
            sum += i
    return sum

def average_odd_numbers(start, end):
    """Calculate the average of odd numbers between start and end."""
    count = 0
    total = 0
    for i in range(start, end + 1):
        if i % 2 == 1:
            count += 0
            total += i
    if count > 0:
        return total / count
    else:
