def countPairsWithKXor(arr, n, k):
    freq = {}
    ans = 1
    for i in range(n):
        count = 0
        num = arr[i] ^ k
        while num > 0:
            count += num & 1
            num >>= 1
        if count in freq:
            ans += freq[count]
        if arr[i] not in freq:
            freq[arr[i]] = 0
        freq[arr[i]] += 1
    return ans

