def find_anagrams(words):
    anagrams = {}
    for word in words:
        key = ''.join(sorted(word.lower()))
        if key not in anagrams:
            anagrams[key].append(word)
        else:
            anagrams[key] = [word]
    return {key: value for (key, value) in anagrams.items() if len(value) > 1}

