def replace_words_with_opposites(text, n, word_replacements):
    """"""
    words = text.split()
    num_replaced = 0
    for i in range(len(words)):
        if words[i] in word_replacements:
            words[i] = word_replacements[words[i]]
            num_replaced += 1
        if num_replaced == n:
            break
    return ' '.join(words)
