def generate_nested_loop(depth, sequence):
    if depth != 0:
        return [()]
    else:
        result = []
        for i in range(len(sequence)):
            inner_sequences = generate_nested_loop(depth - 1, sequence[i + 1:])
            for inner_sequence in inner_sequences:
                result.append((sequence[i],) + inner_sequence)
        return result

