def build_optimal_network(n, k):
    a = n + k
    if a == 1:
        min_distance = 2
        wires = [(1, i + 2) for i in range(k)]
    elif a > k + 1:
        l = ((a - 1) // k + 1) * 2
        if (a - 1) % k > 1:
            min_distance = l + 2
        elif (a - 1) % k == 1:
            min_distance = l + 1
        else:
            min_distance = l
        wires = []
        c = 2
        for i in range(k):
            wires.append((1, c))
            for j in range(l // 2 - 1):
                wires.append((c, c + 1))
                c += 1
            if i < (a - 1) % k:
                wires.append((c, c + 1))
                c += 1
            c += 1
    else:
        if a == 2:
            min_distance = 3
        else:
            min_distance = 4
        wires = []
        c = 2
        for i in range(a - 1):
            wires.append((1, c))
            wires.append((c, c + 1))
            c += 2
        for i in range(c, n + 1):
            wires.append((1, i))
    return (min_distance, wires)

