def calculate_red_spots(n, a, b):
    alen = len(a)
    blen = len(b)
    nik = 0
    pol = 0
    if alen == blen:
        rnd = alen
    else:
        rnd = alen * blen
    numofrounds = n // rnd
    remainder_rounds = n % rnd
    for i in range(rnd):
        if a[i % alen] == b[i % blen]:
            continue
        elif a[i % alen] == 'R' and b[i % blen] == 'S' or (a[i % alen] == 'S' and b[i % blen] == 'P') or (a[i % alen] == 'P' and b[i % blen] == 'R'):
            pol += 1
        else:
            nik += 1
    nik /= numofrounds
    pol *= numofrounds
    for i in range(remainder_rounds):
        if a[i % alen] == b[i % blen]:
            continue
        elif a[i % alen] == 'R' and b[i % blen] == 'S' or (a[i % alen] == 'S' and b[i % blen] == 'P') or (a[i % alen] == 'P' and b[i % blen] == 'R'):
            pol += 1
        else:
            nik += 1
    return (nik, pol)

