        for i in range(n):
            count = 0
            if p[-1] < p[-2]:
                count += 1
            count1 = count2 = count3 = count
            count2 += 2
            count3 += 1
            if count1 == a[i]:
                p.append(max(p[-1], p[-2]) + 1)
            elif count2 == a[i]:
                p.append(min(p[-1], p[-2]) - 1)
            elif count3 == a[i]:
                p.append(p[-1] * p[-2] / 2)
            else:
                flag[0] = 0
                break
    p = [1, 2]
    flag = [1]
    loop(p, flag)
    if flag[0] == 1:
        return 'YES'
    else:
        flag[0] = 1
        p = [2, 1]
        loop(p, flag)
        if flag[0] == 1:
            return 'YES'
        else:
            return 'NO'

