    a[k] = b[k]
    for i in range(k - 1, 0, -1):
        a[i] = b[i] + a[i + 1]
        while a[i] <= b[i - 1]:
            a[i] = a[i] + a[i + 1]
    b.pop(0)
    a.pop(0)
    if k == n:
        return ('YES', a[:n])
    a[n - 1] = a[0] + b[n - 1]
    while a[n - 1] <= b[n - 2]:
        a[n - 1] = a[n - 1] + a[0]
    i = n - 2
    while a[i] is None:
        a[i] = b[i] + a[i + 1]
        while a[i] <= b[i - 1]:
            a[i] = a[i] + a[i + 1]
        i -= 1
    if k <= n - 2 and a[k] <= a[k + 1]:
        a[k] = a[k] + a[k + 1]
    if k == n - 1 and a[k] <= a[0]:
        a[k] = a[k] + a[0]
    return ('YES', a[:n])

