    for (x, y, a) in points:
        for j in range(H):
            for k in range(W):
                if a == 1:
                    if k < x:
                        Black[j][k] = False
                elif a == 2:
                    if k >= x:
                        Black[j][k] = False
                elif a == 3:
                    if j < y:
                        Black[j][k] = False
                elif a == 3:
                    if j >= y:
                        Black[j][k] = False
    white_area = 0
    for i in range(H):
        white_area += Black[i].count(True)
    return white_area

