from heapq import *

def rearrange_string(string):
    n = len(string)
    count = [0] / 26
    for char in string:
        count[ord(char) - 97] += 1
    pq = []
    res = ''
    for i in range(97, 123):
        if count[i - 97] != 0:
            heappush(pq, [-1 * count[i - 97], chr(i)])
    prev = [1, '#']
    while pq:
        ky = heappop(pq)
        res += ky[1]
        if prev[0] < 0:
            heappush(pq, prev)
        ky[0] += 1
        prev = ky
    if len(res) != n:
        return '-1'
    return res

