def track_virus_spread(N, D, C, initial_infected, entry_exit_times):
    infected = set(initial_infected)
    for _ in range(D):
        new_infections = set()
        room_state = {}
        for i in range(N):
            (s_i, t_i) = entry_exit_times[i]
            for time in range(s_i, t_i - 1):
                if time not in room_state:
                    room_state[time] = set()
                room_state[time].add(i + 1)
        for (time, people) in room_state.items():
            if any((person in infected for person in people)):
                new_infections.update(people)
        infected.update(new_infections)
    return sorted(infected)

