def predict_robot_explosions(x, y, x0, y0, commands):
    field = [[0 for _ in range(y + 2)] for _ in range(x + 2)]
    for i in range(y + 2):
        field[0][i] = -1
        field[x + 1][i] = -1
    for i in range(x + 2):
        field[i][0] = -1
        field[i][y + 1] = -1
    field[x0][y0] = 1
    result = [1]
    nr_explore = 1
    for move in commands:
        (dx, dy) = (0, 0)
        if move == 'U':
            dx = -1
        elif move == 'D':
            dx = 1
        elif move == 'R':
            dy = 1
        elif move == 'L':
            dy = -1
        if field[x0 + dx][y0 + dy] != -1:
            x0 += dx
            y0 += dy
            if field[x0][y0] == 0:
                nr_explore += 1
                field[x0][y0] = 1
                result.append(1)
            else:
                result.append(0)
        else:
            result.append(0)
    result[-1] -= x * y - nr_explore
    return result

