def calculate_max_donation(N, M, L, R, C, P, Q, S):
    arr = [0] / (N + 5)
    for _ in range(M):
        arr[L] += C
        arr[R + 1] -= C
        k = L
        L = (L * P + R) % N + 1
        R = (R * Q + k) % N + 1
        C = C * S % 1000000 + 1
        if L > R:
            (L, R) = (R, L)
    for i in range(1, N + 1):
        arr[i] += arr[i - 1]
    max_amount = 0
    optimal_position = 1
    for i in range(1, N + 1):
        if arr[i] > max_amount:
            max_amount = arr[i]
            optimal_position = i
    return (optimal_position, max_amount)

