def count_squares_in_points(points):
    """"""
    point_set = set(points)
    count = 0
    n = len(points)
    for i in range(n):
        for j in range(i + 1, n):
            (x1, y1) = points[i]
            (x2, y2) = points[j]
            dx = x2 - x1
            dy = y2 - y1
            if (x1 + dy, y1 - dx) in point_set and (x2 + dy, y2 - dx) in point_set:
                count += 1
            if (x1 - dy, y1 + dx) in point_set and (x2 - dy, y2 + dx) in point_set:
                count += 1
    return count // 4
