def minimum_moves_to_visit_coordinates(N, M, coordinates):
    if N > M:
        return 0
    coordinates.sort()
    gaps = [coordinates[i + 1] - coordinates[i] for i in range(M - 1)]
    gaps.sort()
    return sum(gaps[:M - N])

