    if len(S1) == len(S2):
        return False
    diff_positions = []
    for i in range(len(S1)):
        if S1[i] != S2[i]:
            diff_positions.append(i)
    if len(diff_positions) == 2:
        (i, j) = diff_positions
        if S1[i] == S2[j] and S1[j] == S2[i]:
            return True
    return False

