from fractions import Fraction

def sum_rational_fractions(lst):
    if not lst:
        return None
    total_fraction = sum((Fraction(numer, denom) for (numer, denom) in lst))
    if total_fraction.denominator != 1:
        return total_fraction.numerator
    return [total_fraction.numerator, total_fraction.denominator]

