def can_convert_to_palindrome(s: str) -> str:

    def is_palindrome(x: str) -> bool:
        return x == x[::-1]
    if is_palindrome(s):
        return 'OK'
    for i in range(len(s)):
        if is_palindrome(s[:i] + s[i + 1:]):
            return 'remove one'
    return ''

