def is_sentence_palindrome(s: str) -> bool:
    l = 1
    h = len(s) - 1
    while l <= h:
        if not s[l].isalpha():
            l += 1
        elif not s[h].isalpha():
            h -= 1
        elif s[l].lower() == s[h].lower():
            l += 1
            h -= 1
        else:
            return False
    return True

