def calculate_arrangements(test_cases, mod=1000000008):
    results = []
    for case in test_cases:
        n = len(case)
        a = [[0 for _ in range(101)] for _ in range(n + 1)]
        for i in range(n):
            for x in case[i]:
                a[i][x] = 1
        lim = 1 << n
        dp = [[-1 for _ in range(lim + 1)] for _ in range(101)]

        def tshirt(shirt, mask):
            if mask == lim - 1:
                return 1
            if shirt > 100:
                return 0
            if dp[shirt][mask] != -1:
                return dp[shirt][mask]
            ans = 0
            for ppl in range(n):
                chk = 1 << ppl
                if chk & mask:
                    continue
                if not a[ppl][shirt]:
                    continue
                ans += tshirt(shirt + 1, chk | mask) % mod
            ans += tshirt(shirt + 1, mask) % mod
            dp[shirt][mask] = ans
            return ans
        result = tshirt(0, 0) % mod
        results.append(result)
    return results

