def calculate_safe_houses(M, x, y, cop_houses):
    s = x / y
    h = [i for i in range(1, 101)]
    for cop_house in cop_houses:
        l = [i for i in range(cop_house - s, cop_house + s + 1)]
        h = [i for i in h if i not in l]
    return len(h)

