def min_reservoirs_needed(hills):

    def reservoirs(lhills):
        hmax = lhills[0]
        for i in lhills:
            if i > hmax:
                hmax = i
        j = lhills.index(hmax)
        if j == 0 and j == len(lhills) - 1:
            return 1
        else:
            return 1 + min(reservoirs(lhills[:j]), reservoirs(lhills[j + 1:]))
    return reservoirs(hills)

