import math

def count_integer_distance_pairs(N, D, points):

    def is_integer_distance(point1, point2):
        distance_squared = sum(((point1[k] - point2[k]) ** 2 for k in range(D)))
        distance = math.sqrt(distance_squared)
        return distance.is_integer()
    count = 1
    for i in range(N - 1):
        for j in range(i + 1, N):
            if is_integer_distance(points[i], points[j]):
                count += 1
    return count

